BEGIN -- Validating actions IF @Action NOT IN ( 'A' , 'U' , 'D' ) BEGIN SELECT CAST(0 AS BIT) [IsSuccess] , 'Invalid operation requested.' [Message] , 500 [StatusCode] , - 1 [Data]
RETURN; END
-- In add mode ignoring primary key value SELECT @SessionInvitationId = CASE WHEN @Action = 'A' THEN 0 ELSE ISNULL(@SessionInvitationId, 0) END;
-- In Update/Delete mode checking existance of records IF @Action IN ( 'U' , 'D' ) AND NOT EXISTS ( SELECT TOP 1 1 FROM [cms].[GroupSessionInvitations] t WHERE t.[SessionInvitationId] = @SessionInvitationId AND t.[IsActive] = 1 AND t.[IsDeleted] = 0 ) BEGIN SELECT CAST(0 AS BIT) [IsSuccess] , 'Group Session Invitations does not exists.' [Message] , 500 [StatusCode] , - 1 [Data]
