SELECT CAST(1 AS BIT) [IsSuccess] , 'Group Session Invitations deleted successfully.' [Message] , 200 [StatusCode] , @SessionInvitationId [Data]; END END

BEGIN SET @SessionKey = ISNULL(LTRIM(RTRIM(@SessionKey)), ''); SET @AppointmentId = ISNULL(@AppointmentId, 0);
IF @SessionKey = '' AND @AppointmentId = 0 BEGIN SELECT CAST(0 AS BIT) [IsSuccess] , 'Session Key or Account id not provided.' [Message] , 404 [StatusCode] , -1 [Data]; RETURN; END
IF @SessionKey <> '' BEGIN SELECT @SessionId = gs.[SessionId] FROM [cms].[GroupSessions] gs WHERE gs.[SessionKey] = @SessionKey AND gs.[OrganizationId] = @OrganizationId AND gs.[IsActive] = 1 AND gs.[IsDeleted] = 0; END
IF @AppointmentId > 0 BEGIN SELECT @SessionId = gs.[SessionId] FROM [cms].[GroupSessions] gs JOIN [cms].[GroupSessionInvitations] gsi ON gs.[SessionId] = gsi.[SessionId] AND gsi.[OrganizationId] = @OrganizationId AND gsi.[AppointmentId] = @AppointmentId AND gsi.[IsActive] = 1 AND gsi.[IsDeleted] = 0 WHERE gs.[OrganizationId] = @OrganizationId AND gs.[IsActive] = 1 AND gs.[IsDeleted] = 0; END
IF @SessionId = 0 BEGIN SELECT CAST(0 AS BIT) [IsSuccess] , 'Session not found.' [Message] , 404 [StatusCode] , -1 [Data]; RETURN; END
INSERT INTO [cms].[GroupSessionRecordings] ( [SessionId] , [ArchiveId] , [CreatedBy] , [CreatedOn] , [CreatedIpAddress] , [IsActive] , [IsDeleted] ) SELECT @SessionId [SessionId] , @ArchiveId [ArchiveId] , @UserId [CreatedBy] , GETUTCDATE() [CreatedOn] , @IpAddress [CreatedIpAddress] , 1 [IsActive] , 0 [IsDeleted];
SELECT @SessionRecordingId = SCOPE_IDENTITY();
SELECT CAST(1 AS BIT) [IsSuccess] , 'Group Session Recordings saved successfully.' [Message] , 200 [StatusCode] , @SessionRecordingId [Data]; END

BEGIN -- Validating actions IF @Action NOT IN ( 'A' , 'U' , 'D' ) BEGIN SELECT CAST(0 AS BIT) [IsSuccess] , 'Invalid operation requested.' [Message] , 500 [StatusCode] , - 1 [Data]
RETURN; END
-- In add mode ignoring primary key value SELECT @SessionId = CASE WHEN @Action = 'A' THEN 0 ELSE ISNULL(@SessionId, 0) END;
-- In Update/Delete mode checking existance of records IF @Action IN ( 'U' , 'D' ) AND NOT EXISTS ( SELECT TOP 1 1 FROM [cms].[GroupSessions] t WHERE t.[SessionId] = @SessionId AND t.[IsActive] = 1 AND t.[IsDeleted] = 0 ) BEGIN SELECT CAST(0 AS BIT) [IsSuccess] , 'Group Sessions does not exists.' [Message] , 500 [StatusCode] , - 1 [Data]
RETURN; END
IF @Action = 'A' BEGIN INSERT INTO [cms].[GroupSessions] ( [SessionKey] , [StartTime] , [EndTime] , [OrganizationId] , [CreatedBy] , [CreatedOn] , [CreatedIpAddress] , [IsActive] , [IsDeleted] ) SELECT @SessionKey [SessionKey] , @StartTime [StartTime] , @EndTime [EndTime] , @OrganizationId [OrganizationId] , @UserId [CreatedBy] , GETUTCDATE() [CreatedOn] , @IpAddress [CreatedIpAddress] , 1 [IsActive] , 0 [IsDeleted];
SELECT @SessionId = SCOPE_IDENTITY();
SELECT CAST(1 AS BIT) [IsSuccess] , 'Group Sessions saved successfully.' [Message] , 200 [StatusCode] , @SessionId [Data]; END
IF @Action = 'U' BEGIN UPDATE t SET t.[SessionKey] = @SessionKey , t.[StartTime] = @StartTime , t.[EndTime] = @EndTime , t.[OrganizationId] = @OrganizationId , t.[LastModifiedBy] = @UserId , t.[LastModifiedOn] = GETUTCDATE() , t.[LastModifiedIpAddress] = @IpAddress FROM [cms].[GroupSessions] t WHERE t.[IsActive] = 1 AND t.[IsDeleted] = 0 AND t.[SessionId] = @SessionId;
SELECT CAST(1 AS BIT) [IsSuccess] , 'Group Sessions updated successfully.' [Message] , 200 [StatusCode] , @SessionId [Data]; END
IF @Action = 'D' BEGIN UPDATE t SET t.[IsActive] = 0 , t.[IsDeleted] = 1 , t.[DeletedBy] = @UserId , t.[DeletedOn] = GETUTCDATE() , t.[DeletedIpAddress] = @IpAddress FROM [cms].[GroupSessions] t WHERE t.[IsActive] = 1 AND t.[IsDeleted] = 0 AND t.[SessionId] = @SessionId;
SELECT CAST(1 AS BIT) [IsSuccess] , 'Group Sessions deleted successfully.' [Message] , 200 [StatusCode] , @SessionId [Data]; END END

BEGIN -- Validating actions IF @Action NOT IN ( 'A' , 'U' , 'D' ) BEGIN SELECT CAST(0 AS BIT) [IsSuccess] , 'Invalid operation requested.' [Message] , 500 [StatusCode] , - 1 [Data]
RETURN; END
-- In add mode ignoring primary key value SELECT @SessionTokenId = CASE WHEN @Action = 'A' THEN 0 ELSE ISNULL(@SessionTokenId, 0) END;
-- In Update/Delete mode checking existance of records IF @Action IN ( 'U' , 'D' ) AND NOT EXISTS ( SELECT TOP 1 1 FROM [cms].[GroupSessionTokens] t WHERE t.[SessionTokenId] = @SessionTokenId AND t.[IsActive] = 1 AND t.[IsDeleted] = 0 ) BEGIN SELECT CAST(0 AS BIT) [IsSuccess] , 'Group Session Tokens does not exists.' [Message] , 500 [StatusCode] , - 1 [Data]
RETURN; END
IF @Action = 'A' BEGIN INSERT INTO [cms].[GroupSessionTokens] ( [SessionId] , [SessionInvitationId] , [TokenKey] , [TokenExpiry] , [OrganizationId] , [CreatedBy] , [CreatedOn] , [CreatedIpAddress] , [IsActive] , [IsDeleted] ) SELECT @SessionId [SessionId] , @SessionInvitationId [SessionInvitationId] , @TokenKey [TokenKey] , @TokenExpiry [TokenExpiry] , @OrganizationId [OrganizationId] , @UserId [CreatedBy] , GETUTCDATE() [CreatedOn] , @IpAddress [CreatedIpAddress] , 1 [IsActive] , 0 [IsDeleted];
SELECT @SessionTokenId = SCOPE_IDENTITY();
SELECT CAST(1 AS BIT) [IsSuccess] , 'Group Session Tokens saved successfully.' [Message] , 200 [StatusCode] , @SessionTokenId [Data]; END
IF @Action = 'U' BEGIN UPDATE t SET t.[SessionId] = @SessionId , t.[SessionInvitationId] = @SessionInvitationId , t.[TokenKey] = @TokenKey , t.[TokenExpiry] = @TokenExpiry , t.[OrganizationId] = @OrganizationId , t.[LastModifiedBy] = @UserId , t.[LastModifiedOn] = GETUTCDATE() , t.[LastModifiedIpAddress] = @IpAddress FROM [cms].[GroupSessionTokens] t WHERE t.[IsActive] = 1 AND t.[IsDeleted] = 0 AND t.[SessionTokenId] = @SessionTokenId;
SELECT CAST(1 AS BIT) [IsSuccess] , 'Group Session Tokens updated successfully.' [Message] , 200 [StatusCode] , @SessionTokenId [Data]; END
IF @Action = 'D' BEGIN UPDATE t SET t.[IsActive] = 0 , t.[IsDeleted] = 1 , t.[DeletedBy] = @UserId , t.[DeletedOn] = GETUTCDATE() , t.[DeletedIpAddress] = @IpAddress FROM [cms].[GroupSessionTokens] t WHERE t.[IsActive] = 1 AND t.[IsDeleted] = 0 AND t.[SessionTokenId] = @SessionTokenId;
SELECT CAST(1 AS BIT) [IsSuccess] , 'Group Session Tokens deleted successfully.' [Message] , 200 [StatusCode] , @SessionTokenId [Data]; END END
